import React, { useState, useEffect } from 'react';

const PET_API = 'http://localhost:8090/bd_petcare/api/pets';

function PetForm() {
  const [formData, setFormData] = useState({
    name: '',
    species: '',
    breed: '',
    birthDate: '',
    owner: {
      ownerId: ''
    }
  });

  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    // Obtener el usuario desde localStorage
    const usuario = JSON.parse(localStorage.getItem('usuario'));

    if (usuario && usuario.duenio && usuario.duenio.idDuenio) {
      // Asignar autom치ticamente el ownerId
      setFormData(prev => ({
        ...prev,
        owner: { ownerId: usuario.duenio.idDuenio }
      }));
    } else {
      console.warn("El usuario no tiene un duenio asociado.");
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch(`${PET_API}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
      .then(res => {
        if (res.ok) {
          setSuccessMessage('Mascota registrada correctamente');
          setFormData(prev => ({
            name: '',
            species: '',
            breed: '',
            birthDate: '',
            owner: { ownerId: prev.owner.ownerId } // Mantener el due침o
          }));
        } else {
          return res.text().then(text => { throw new Error(text) });
        }
      })
      .catch(err => {
        console.error('Error al registrar mascota:', err);
        setSuccessMessage('Error al registrar mascota');
      });
  };

  return (
    <div>
      <h2>Registrar Nueva Mascota</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre:</label>
          <input name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Especie:</label>
          <input name="species" value={formData.species} onChange={handleChange} required />
        </div>
        <div>
          <label>Raza:</label>
          <input name="breed" value={formData.breed} onChange={handleChange} required />
        </div>
        <div>
          <label>Fecha de nacimiento:</label>
          <input type="date" name="birthDate" value={formData.birthDate} onChange={handleChange} required />
        </div>

        {/* Due침o oculto - s칩lo informativo */}
        <div style={{ marginTop: '1em' }}>
          <strong>ID Due침o asignado:</strong> {formData.owner.ownerId || 'No disponible'}
        </div>

        <button type="submit" style={{ marginTop: '1em' }}>Registrar</button>
      </form>

      {successMessage && <p>{successMessage}</p>}
    </div>
  );
}

export default PetForm;
