import React, { useState, useEffect } from 'react';

const PET_API = 'http://localhost:8090/bd_petcare/api/pets';
const OWNER_API = 'http://localhost:8090/bd_petcare/api/owners';

function PetForm() {
  const [formData, setFormData] = useState({
    name: '',
    species: '',
    breed: '',
    birthDate: '',
    owner: { ownerId: '' }
  });

  const [successMessage, setSuccessMessage] = useState('');
  const [error, setError] = useState('');
  const [loadingOwner, setLoadingOwner] = useState(true);

  useEffect(() => {
    const usuarioGuardado = localStorage.getItem('usuario');
    if (usuarioGuardado) {
      const usuario = JSON.parse(usuarioGuardado);

      fetch(`${OWNER_API}/user/${usuario.userId}`)
        .then(res => {
          if (!res.ok) throw new Error('No se pudo obtener el dueño');
          return res.json();
        })
        .then(owner => {
          setFormData(prev => ({
            ...prev,
            owner: { ownerId: owner.ownerId }
          }));
          setLoadingOwner(false);
        })
        .catch(err => {
          console.error('Error al obtener dueño:', err);
          setError('Error al obtener el dueño');
          setLoadingOwner(false);
        });
    } else {
      setError('Usuario no logueado');
      setLoadingOwner(false);
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.owner.ownerId) {
      setError("No se ha asociado un dueño válido");
      return;
    }

    fetch(`${PET_API}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
      .then(res => {
        if (!res.ok) return res.text().then(text => { throw new Error(text); });
        return res.json();
      })
      .then(() => {
        setSuccessMessage('Mascota registrada correctamente');
        setFormData({
          name: '',
          species: '',
          breed: '',
          birthDate: '',
          owner: { ownerId: formData.owner.ownerId }
        });
        setError('');
      })
      .catch(err => {
        console.error('Error al registrar mascota:', err);
        setError('Error al registrar mascota');
        setSuccessMessage('');
      });
  };

  if (loadingOwner) {
    return <p>Cargando dueño...</p>;
  }

  return (
    <div>
      <h2>Registrar Nueva Mascota</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre:</label>
          <input name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Especie:</label>
          <input name="species" value={formData.species} onChange={handleChange} required />
        </div>
        <div>
          <label>Raza:</label>
          <input name="breed" value={formData.breed} onChange={handleChange} required />
        </div>
        <div>
          <label>Fecha de nacimiento:</label>
          <input type="date" name="birthDate" value={formData.birthDate} onChange={handleChange} required />
        </div>
        <button type="submit">Registrar</button>
      </form>
    </div>
  );
}

export default PetForm;
