import React, { useState, useEffect } from 'react';

const PET_API = 'http://localhost:8090/bd_petcare/api/pets';
const OWNER_API = 'http://localhost:8090/bd_petcare/api/owners';

function PetForm() {
  const [formData, setFormData] = useState({
    name: '',
    species: '',
    breed: '',
    birthDate: '',
    owner: {
      ownerId: ''
    }
  });

  const [successMessage, setSuccessMessage] = useState('');
  const [loading, setLoading] = useState(true);

useEffect(() => {
  if (selectedUserId) {
    fetch(`http://localhost:8090/bd_petcare/api/owner/user/${selectedUserId}`)
      .then(res => res.json())
      .then(data => {
        if (data) {
          setOwnerData(data); // aquí cargas los datos del dueño
        }
      })
      .catch(err => console.error(err));
  }
}, [selectedUserId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch(`${PET_API}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
      .then(res => {
        if (res.ok) {
          setSuccessMessage('Mascota registrada correctamente');
          setFormData(prev => ({
            ...prev,
            name: '',
            species: '',
            breed: '',
            birthDate: ''
            // No reiniciamos ownerId, ya que es del usuario logueado
          }));
        } else {
          return res.text().then(text => { throw new Error(text); });
        }
      })
      .catch(err => {
        console.error('Error al registrar mascota:', err);
        setSuccessMessage('Error al registrar mascota');
      });
  };

  if (loading) return <p>Cargando formulario...</p>;

  return (
    <div>
      <h2>Registrar Nueva Mascota</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre:</label>
          <input name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Especie:</label>
          <input name="species" value={formData.species} onChange={handleChange} required />
        </div>
        <div>
          <label>Raza:</label>
          <input name="breed" value={formData.breed} onChange={handleChange} required />
        </div>
        <div>
          <label>Fecha de nacimiento:</label>
          <input type="date" name="birthDate" value={formData.birthDate} onChange={handleChange} required />
        </div>

        {/* Mostrar el ownerId actual solo como información */}
        <div>
          <label>Dueño:</label>
          <input type="text" value={formData.owner.ownerId} readOnly />
        </div>

        <button type="submit">Registrar</button>
      </form>

      {successMessage && <p>{successMessage}</p>}
    </div>
  );
}

export default PetForm;
