import React, { useState, useEffect } from 'react';

const PET_API = 'http://localhost:8090/bd_petcare/api/pets';
const OWNER_BY_USER_API = 'http://localhost:8090/bd_petcare/api/owners/user'; // + /{userId}

const styles = {
  pageContainer: {
    minHeight: '100vh',
    backgroundColor: '#ff4d4d', // rojo vibrante de fondo
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '40px 20px',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  card: {
    backgroundColor: 'white',
    borderRadius: '20px',
    boxShadow: '0 12px 32px rgba(255, 0, 0, 0.3)',
    maxWidth: '600px',
    width: '100%',
    padding: '30px 40px 40px',
  },
  header: {
    textAlign: 'center',
    marginBottom: '25px',
  },
  title: {
    fontSize: '2.8rem',
    color: '#cc0000', // rojo oscuro
    marginBottom: '6px',
    fontWeight: '900',
    letterSpacing: '1.5px',
  },
  subtitle: {
    fontSize: '1.1rem',
    color: '#007a29', // verde oscuro
    fontWeight: '600',
  },
  imgContainer: {
    textAlign: 'center',
    marginBottom: '25px',
  },
  petImage: {
    width: '180px',
    borderRadius: '16px',
    boxShadow: '0 8px 20px rgba(0, 122, 41, 0.5)', // sombra verde
  },
  formGroup: {
    marginBottom: '20px',
  },
  label: {
    display: 'block',
    marginBottom: '8px',
    fontWeight: '700',
    color: '#cc0000',
    fontSize: '1.1rem',
    textTransform: 'uppercase',
    letterSpacing: '1px',
  },
  input: {
    width: '100%',
    padding: '12px 16px',
    borderRadius: '12px',
    border: '2.5px solid #cc0000',
    fontSize: '1rem',
    outline: 'none',
    transition: 'border-color 0.3s',
  },
  inputFocus: {
    borderColor: '#007a29',
  },
  select: {
    width: '100%',
    padding: '12px 16px',
    borderRadius: '12px',
    border: '2.5px solid #cc0000',
    fontSize: '1rem',
    outline: 'none',
    backgroundColor: '#fff',
    cursor: 'pointer',
  },
  button: {
    width: '100%',
    backgroundColor: '#007a29',
    color: '#fff',
    padding: '16px',
    fontSize: '1.3rem',
    fontWeight: '900',
    border: 'none',
    borderRadius: '16px',
    cursor: 'pointer',
    marginTop: '15px',
    letterSpacing: '1.5px',
    transition: 'background-color 0.3s',
  },
  buttonDisabled: {
    backgroundColor: '#9acd9a',
    cursor: 'not-allowed',
  },
  successMsg: {
    marginTop: '20px',
    fontWeight: '700',
    fontSize: '1.1rem',
    color: '#007a29',
    textAlign: 'center',
  },
  errorMsg: {
    marginTop: '20px',
    fontWeight: '700',
    fontSize: '1.1rem',
    color: '#cc0000',
    textAlign: 'center',
  },
  userInfo: {
    marginTop: '20px',
    fontStyle: 'italic',
    color: '#cc0000',
    fontWeight: '600',
    textAlign: 'center',
  },
};

function PetForm() {
  const [formData, setFormData] = useState({
    name: '',
    species: '',
    breed: '',
    birthDate: '',
    owner: { ownerId: '' },
    photo: null,
  });

  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [usuario, setUsuario] = useState(null);
  const [loadingOwner, setLoadingOwner] = useState(true);
  const [imagePreview, setImagePreview] = useState(null);

  useEffect(() => {
    const usuarioGuardado = localStorage.getItem('usuario');
    if (usuarioGuardado) {
      const user = JSON.parse(usuarioGuardado);
      setUsuario(user);

      if (user.role === 'duenio') {
        fetch(`${OWNER_BY_USER_API}/${user.userId}`)
          .then(res => {
            if (!res.ok) throw new Error('No se pudo obtener el dueño');
            return res.json();
          })
          .then(owner => {
            setFormData(prev => ({
              ...prev,
              owner: { ownerId: owner.ownerId }
            }));
            setErrorMessage('');
          })
          .catch(err => {
            console.error('Error obteniendo ownerId:', err);
            setErrorMessage('Error al cargar datos del dueño');
          })
          .finally(() => setLoadingOwner(false));
      } else {
        setLoadingOwner(false);
      }
    }
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    if (name === 'photo' && files.length > 0) {
      setFormData(prev => ({ ...prev, photo: files[0] }));
      setImagePreview(URL.createObjectURL(files[0]));
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSuccessMessage('');
    setErrorMessage('');

    // Aquí solo enviamos JSON. Para foto habría que usar FormData y backend que la acepte.
    fetch(`${PET_API}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    })
      .then(res => {
        if (res.ok) {
          setSuccessMessage('¡Mascota registrada correctamente!');
          setFormData(prev => ({
            name: '',
            species: '',
            breed: '',
            birthDate: '',
            owner: { ownerId: prev.owner.ownerId },
            photo: null,
          }));
          setImagePreview(null);
        } else {
          return res.text().then(text => { throw new Error(text); });
        }
      })
      .catch(err => {
        console.error('Error al registrar mascota:', err);
        setErrorMessage('Error al registrar mascota');
      });
  };

  if (!usuario) return <p style={{ color: 'white', textAlign: 'center' }}>Cargando usuario...</p>;
  if (loadingOwner) return <p style={{ color: 'white', textAlign: 'center' }}>Cargando datos...</p>;

  if (usuario.role !== 'duenio') {
    return (
      <div style={{ color: 'white', textAlign: 'center', marginTop: '40px', fontWeight: '700' }}>
        <h2>Bienvenido, {usuario.role.charAt(0).toUpperCase() + usuario.role.slice(1)}</h2>
        <p>Aquí puedes ver tus asignaciones y tareas.</p>
      </div>
    );
  }

  return (
    <div style={styles.pageContainer}>
      <div style={styles.card}>
        <div style={styles.header}>
          <h1 style={styles.title}>Registra a tu Mascota</h1>
          <p style={styles.subtitle}>Completa los datos para brindarle el mejor cuidado</p>
        </div>

        <div style={styles.imgContainer}>
          <img
            src="https://images.unsplash.com/photo-1552053831-71594a27632d?ixlib=rb-1.2.1&auto=format&fit=crop&w=612&q=80"
            alt="Mascota feliz"
            style={styles.petImage}
          />
        </div>

        <form onSubmit={handleSubmit}>

          <div style={styles.formGroup}>
            <label style={styles.label} htmlFor="name">Nombre de la mascota</label>
            <input
              type="text"
              id="name"
              name="name"
              style={styles.input}
              placeholder="Ej: Max"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label} htmlFor="species">Especie</label>
            <select
              id="species"
              name="species"
              style={styles.select}
              value={formData.species}
              onChange={handleChange}
              required
            >
              <option value="" disabled>Selecciona una especie</option>
              <option value="dog">Perro</option>
              <option value="cat">Gato</option>
              <option value="bird">Ave</option>
              <option value="rabbit">Conejo</option>
              <option value="other">Otra</option>
            </select>
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label} htmlFor="breed">Raza</label>
            <input
              type="text"
              id="breed"
              name="breed"
              style={styles.input}
              placeholder="Ej: Golden Retriever"
              value={formData.breed}
              onChange={handleChange}
              required
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label} htmlFor="birthDate">Fecha de nacimiento</label>
            <input
              type="date"
              id="birthDate"
              name="birthDate"
              style={styles.input}
              value={formData.birthDate}
              onChange={handleChange}
              required
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label} htmlFor="photo">Foto de tu Mascota (Opcional)</label>
            <input
              type="file"
              id="photo"
              name="photo"
              accept="image/*"
              onChange={handleChange}
              style={{ ...styles.input, padding: '6px 10px' }}
            />
            {imagePreview && (
              <img
                src={imagePreview}
                alt="Previsualización"
                style={{ marginTop: '15px', maxWidth: '100%', borderRadius: '12px' }}
              />
            )}
          </div>

          <button
            type="submit"
            style={{
              ...styles.button,
              ...(formData.name && formData.species && formData.breed && formData.birthDate ? {} : styles.buttonDisabled),
            }}
            disabled={!(formData.name && formData.species && formData.breed && formData.birthDate)}
          >
            Registrar Mascota
          </button>

          {successMessage && <p style={styles.successMsg}>{successMessage}</p>}
          {errorMessage && <p style={styles.errorMsg}>{errorMessage}</p>}

          <p style={styles.userInfo}>
            Usuario: {usuario.name} ({usuario.role.toUpperCase()})
          </p>
        </form>
      </div>
    </div>
  );
}

export default PetForm;
