import React, { useState, useEffect } from 'react';
import UserTable from '../componentes/UserTable';
import OwnerTable from '../componentes/OwnerTable';
import PetTable from '../componentes/PetTable';
import './dashboard.css'; // Asegúrate de crear este archivo

const USER_API = 'http://localhost:8090/bd_petcare/api/users';
const OWNER_API = 'http://localhost:8090/bd_petcare/api/owners';
const PET_API = 'http://localhost:8090/bd_petcare/api/pets';

function Dashboard() {
  const [users, setUsers] = useState([]);
  const [owners, setOwners] = useState([]);
  const [pets, setPets] = useState([]);
  const [usuario, setUsuario] = useState(null);
  const [seccionActiva, setSeccionActiva] = useState('usuarios');

  useEffect(() => {
    const usuarioGuardado = localStorage.getItem('usuario');
    if (usuarioGuardado) {
      setUsuario(JSON.parse(usuarioGuardado));
    }

    fetchUsers();
    fetchOwners();
    fetchPets();
  }, []);

  const fetchUsers = () => {
    fetch(`${USER_API}/all`)
      .then(res => res.json())
      .then(setUsers)
      .catch(err => console.error('Error cargando usuarios:', err));
  };

  const fetchOwners = () => {
    fetch(`${OWNER_API}/all`)
      .then(res => res.json())
      .then(setOwners)
      .catch(err => console.error('Error cargando dueños:', err));
  };

  const fetchPets = () => {
    fetch(`${PET_API}/all`)
      .then(res => res.json())
      .then(setPets)
      .catch(err => console.error('Error cargando mascotas:', err));
  };

  const handleUserDelete = (id) => {
    fetch(`${USER_API}/delete/${id}`, { method: 'DELETE' })
      .then(() => {
        fetchUsers();
        fetchOwners();
        fetchPets();
      })
      .catch(err => console.error('Error al eliminar usuario:', err));
  };

  const handleUserUpdate = (user) => {
    fetch(`${USER_API}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(user)
    })
      .then(() => fetchUsers())
      .catch(err => console.error('Error al actualizar usuario:', err));
  };

  const handleOwnerUpdate = (owner) => {
    fetch(`${OWNER_API}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(owner)
    })
      .then(() => fetchOwners())
      .catch(err => console.error('Error al actualizar dueño:', err));
  };

  const handlePetDelete = (id) => {
    fetch(`${PET_API}/delete/${id}`, { method: 'DELETE' })
      .then(() => fetchPets())
      .catch(err => console.error('Error al eliminar mascota:', err));
  };

  const handlePetUpdate = (pet) => {
    fetch(`${PET_API}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(pet)
    })
      .then(() => fetchPets())
      .catch(err => console.error('Error al actualizar mascota:', err));
  };

  return (
    <div className="dashboard-container">
      <nav className="dashboard-navbar bg-dark text-white d-flex justify-content-between align-items-center px-4 py-2">
        <h3 className="m-0">Panel de Administración - PetCare</h3>
        {usuario && (
          <div className="d-flex align-items-center">
            <i className="bi bi-person-circle me-2" style={{ fontSize: '1.6rem', color: '#ED6436' }}></i>
            <span>Hola, {usuario.name}</span>
          </div>
        )}
      </nav>

      <div className="d-flex">
        <aside className="dashboard-sidebar bg-light p-3">
          <h5 className="text-primary">Secciones</h5>
          <ul className="nav flex-column">
            <li className="nav-item">
              <button onClick={() => setSeccionActiva('usuarios')} className={`nav-link ${seccionActiva === 'usuarios' ? 'active' : ''}`}>
                Usuarios
              </button>
            </li>
            <li className="nav-item">
              <button onClick={() => setSeccionActiva('duenos')} className={`nav-link ${seccionActiva === 'duenos' ? 'active' : ''}`}>
                Dueños
              </button>
            </li>
            <li className="nav-item">
              <button onClick={() => setSeccionActiva('mascotas')} className={`nav-link ${seccionActiva === 'mascotas' ? 'active' : ''}`}>
                Mascotas
              </button>
            </li>
          </ul>
        </aside>

        <main className="dashboard-main p-4 flex-grow-1">
          {seccionActiva === 'usuarios' && (
            <>
              <h4 className="mb-4">Gestión de Usuarios</h4>
              <UserTable users={users} onDelete={handleUserDelete} onUpdate={handleUserUpdate} />
            </>
          )}
          {seccionActiva === 'duenos' && (
            <>
              <h4 className="mb-4">Gestión de Dueños</h4>
              <OwnerTable owners={owners} onUpdate={handleOwnerUpdate} />
            </>
          )}
          {seccionActiva === 'mascotas' && (
            <>
              <h4 className="mb-4">Gestión de Mascotas</h4>
              <PetTable pets={pets} onUpdate={handlePetUpdate} onDelete={handlePetDelete} />
            </>
          )}
        </main>
      </div>
    </div>
  );
}

export default Dashboard;
