import React, { useEffect } from 'react';

const testimoniosHTML = `
  <div class="container-fluid bg-light my-5 p-0 py-5">
    <div class="container p-0 py-5">
      <div class="d-flex flex-column text-center mb-5">
        <h4 class="text-secondary mb-3">Testimonios</h4>
        <h1 class="display-4 m-0">Nuestros clientes <span class="text-primary">dicen</span></h1>
      </div>
      <div class="owl-carousel testimonial-carousel">
        <div class="bg-white mx-3 p-4">
          <div class="d-flex align-items-end mb-3 mt-n4 ml-n4">
            <img class="img-fluid" src="img/testimonial-1.jpg" style="width: 80px; height: 80px;" alt="">
            <div class="ml-3">
              <h5>María López</h5>
              <i>Veterinaria</i>
            </div>
          </div>
          <p class="m-0">El servicio es excelente, siempre atienden con mucha dedicación y cariño a nuestras mascotas. ¡Altamente recomendados!</p>
        </div>
        <div class="bg-white mx-3 p-4">
          <div class="d-flex align-items-end mb-3 mt-n4 ml-n4">
            <img class="img-fluid" src="img/testimonial-2.jpg" style="width: 80px; height: 80px;" alt="">
            <div class="ml-3">
              <h5>Carlos Ramírez</h5>
              <i>Ingeniero</i>
            </div>
          </div>
          <p class="m-0">Llevo a mi perro aquí desde hace años. Siempre he recibido atención de calidad y muy profesional.</p>
        </div>
        <div class="bg-white mx-3 p-4">
          <div class="d-flex align-items-end mb-3 mt-n4 ml-n4">
            <img class="img-fluid" src="img/testimonial-3.jpg" style="width: 80px; height: 80px;" alt="">
            <div class="ml-3">
              <h5>Lucía Fernández</h5>
              <i>Docente</i>
            </div>
          </div>
          <p class="m-0">El equipo de PetCare es muy amable y atento. Mi gato siempre está tranquilo cuando lo llevo.</p>
        </div>
        <div class="bg-white mx-3 p-4">
          <div class="d-flex align-items-end mb-3 mt-n4 ml-n4">
            <img class="img-fluid" src="img/testimonial-4.jpg" style="width: 80px; height: 80px;" alt="">
            <div class="ml-3">
              <h5>Pedro Soto</h5>
              <i>Empresario</i>
            </div>
          </div>
          <p class="m-0">Un lugar limpio, ordenado y con un personal que realmente ama a los animales. Muy recomendable.</p>
        </div>
      </div>
    </div>
  </div>
`;

function Testimonios() {
  useEffect(() => {
    const $ = window.$;
    if ($ && $.fn.owlCarousel) {
      const $carousel = $(".testimonial-carousel");
      // Destruye instancia anterior si existe (por si se vuelve a montar)
      $carousel.trigger('destroy.owl.carousel');
      // Limpia estilos añadidos por owlCarousel
      $carousel.find('.owl-stage-outer').children().unwrap();
      $carousel.removeClass('owl-loaded owl-drag');

      // Inicializa Owl Carousel
      $carousel.owlCarousel({
        center: true,
        autoplay: true,
        smartSpeed: 2000,
        dots: true,
        loop: true,
        responsive: {
          0: { items: 1 },
          576: { items: 1 },
          768: { items: 2 },
          992: { items: 3 }
        }
      });
    }
  }, []);

  return <div dangerouslySetInnerHTML={{ __html: testimoniosHTML }} />;
}

export default Testimonios;
