import React, { useState } from 'react';

function UserTable({ users, onDelete, onUpdate }) {
  const [editUserId, setEditUserId] = useState(null);
  const [formData, setFormData] = useState({});

const handleEditClick = (user) => {
  setEditUserId(user.userId);
  setFormData({
    ...user,
    owner: user.owner || null,  // Mantener referencia al owner
  });
};


  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = () => {
    onUpdate(formData);
    setEditUserId(null);
  };

  return (
    <table border="1" cellPadding="10">
      <thead>
        <tr>
          <th>ID</th><th>Nombre</th><th>Email</th><th>Contrase√±a</th><th>Rol</th><th>Acciones</th>
        </tr>
      </thead>
      <tbody>
        {users.map(user => (
          editUserId === user.userId ? (
            <tr key={user.userId}>
              <td>{user.userId}</td>
              <td><input name="name" value={formData.name} onChange={handleChange} /></td>
              <td><input name="email" value={formData.email} onChange={handleChange} /></td>
              <td><input name="password" value={formData.password} onChange={handleChange} /></td>
              <td><input name="role" value={formData.role} onChange={handleChange} /></td>
              <td>
                <button onClick={handleSubmit}>Guardar</button>
                <button onClick={() => setEditUserId(null)}>Cancelar</button>
              </td>
            </tr>
          ) : (
            <tr key={user.userId}>
              <td>{user.userId}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.password}</td>
              <td>{user.role}</td>
              <td>
                <button onClick={() => handleEditClick(user)}>Editar</button>
                <button onClick={() => onDelete(user.userId)}>Eliminar</button>
              </td>
            </tr>
          )
        ))}
      </tbody>
    </table>
  );
}

export default UserTable;
