import React from 'react';
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';

function Testimonios() {
  const opciones = {
    center: true,
    autoplay: true,
    smartSpeed: 2000,
    dots: true,
    loop: true,
    responsive: {
      0: { items: 1 },
      576: { items: 1 },
      768: { items: 2 },
      992: { items: 3 }
    }
  };

  return (
    <div className="container-fluid bg-light my-5 p-0 py-5">
      <div className="container p-0 py-5">
        <div className="d-flex flex-column text-center mb-5">
          <h4 className="text-secondary mb-3">Testimonios</h4>
          <h1 className="display-4 m-0">Nuestros clientes <span className="text-primary">dicen</span></h1>
        </div>
        <OwlCarousel className="owl-theme" {...opciones}>
          {/* Testimonio 1 */}
          <div className="bg-white mx-3 p-4">
            <div className="d-flex align-items-end mb-3 mt-n4 ml-n4">
              <img className="img-fluid" src="img/testimonial-1.jpg" style={{ width: '80px', height: '80px' }} alt="" />
              <div className="ml-3">
                <h5>María López</h5>
                <i>Veterinaria</i>
              </div>
            </div>
            <p className="m-0">El servicio es excelente, siempre atienden con mucha dedicación y cariño a nuestras mascotas. ¡Altamente recomendados!</p>
          </div>

          {/* Testimonio 2 */}
          <div className="bg-white mx-3 p-4">
            <div className="d-flex align-items-end mb-3 mt-n4 ml-n4">
              <img className="img-fluid" src="img/testimonial-2.jpg" style={{ width: '80px', height: '80px' }} alt="" />
              <div className="ml-3">
                <h5>Carlos Ramírez</h5>
                <i>Ingeniero</i>
              </div>
            </div>
            <p className="m-0">Llevo a mi perro aquí desde hace años. Siempre he recibido atención de calidad y muy profesional.</p>
          </div>

          {/* Agrega los demás testimonios igual */}
        </OwlCarousel>
      </div>
    </div>
  );
}

export default Testimonios;
