// src/pages/Dashboard.js
import React, { useEffect, useState } from 'react';
import UserTable from '../componentes/UserTable';
import OwnerTable from '../componentes/OwnerTable';

const API_USER_URL = 'http://localhost:8090/bd_petcare/api/users';
const API_OWNER_URL = 'http://localhost:8090/bd_petcare/api/owners';

function Dashboard() {
  const [users, setUsers] = useState([]);
  const [owners, setOwners] = useState([]);

  const fetchUsers = () => {
    fetch(`${API_USER_URL}/all`)
      .then(res => res.json())
      .then(setUsers)
      .catch(err => console.error('Error cargando usuarios:', err));
  };

  const fetchOwners = () => {
    fetch(`${API_OWNER_URL}/all`)
      .then(res => res.json())
      .then(setOwners)
      .catch(err => console.error('Error cargando dueños:', err));
  };

  useEffect(() => {
    fetchUsers();
    fetchOwners();
  }, []);

  const handleDelete = (id) => {
    fetch(`${API_USER_URL}/delete/${id}`, {
      method: 'DELETE'
    })
      .then(() => {
        fetchUsers();
        fetchOwners(); // por si también se elimina el dueño
      })
      .catch(err => console.error('Error al eliminar:', err));
  };

  const handleUserUpdate = (user) => {
    fetch(`${API_USER_URL}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(user)
    })
      .then(() => {
        fetchUsers();
        fetchOwners(); // para reflejar cambios si afectan dueño
      })
      .catch(err => console.error('Error al actualizar usuario:', err));
  };

  const handleOwnerUpdate = (owner) => {
    fetch(`${API_OWNER_URL}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(owner)
    })
      .then(fetchOwners)
      .catch(err => console.error('Error al actualizar dueño:', err));
  };

  return (
    <div>
      <h2>Gestión de Usuarios</h2>
      <UserTable users={users} onDelete={handleDelete} onUpdate={handleUserUpdate} />

      <h2 style={{ marginTop: '40px' }}>Gestión de Dueños</h2>
      <OwnerTable owners={owners} onUpdate={handleOwnerUpdate} />
    </div>
  );
}

export default Dashboard;
