import React, { useState, useEffect } from 'react';

const PET_API = 'http://localhost:8090/bd_petcare/api/pets';
const OWNER_API = 'http://localhost:8090/bd_petcare/api/owners';

function PetForm() {
  const [formData, setFormData] = useState({
    name: '',
    species: '',
    breed: '',
    birthDate: '',
    owner: {
      ownerId: ''
    }
  });

  const [successMessage, setSuccessMessage] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const usuarioGuardado = localStorage.getItem('usuario');

    if (usuarioGuardado) {
      const user = JSON.parse(usuarioGuardado);

      // Supongamos que tu backend tiene un endpoint para buscar el dueño por userId:
      fetch(`${OWNER_API}/user/${user.userId}`)
        .then(res => {
          if (!res.ok) throw new Error("No se pudo obtener el dueño");
          return res.json();
        })
        .then(owner => {
          setFormData(prev => ({
            ...prev,
            owner: {
              ownerId: owner.ownerId
            }
          }));
          setLoading(false);
        })
        .catch(err => {
          console.error('Error al obtener dueño:', err);
          setSuccessMessage("Error al obtener el dueño vinculado.");
          setLoading(false);
        });
    } else {
      setSuccessMessage("No hay usuario autenticado.");
      setLoading(false);
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch(`${PET_API}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
      .then(res => {
        if (res.ok) {
          setSuccessMessage('Mascota registrada correctamente');
          setFormData(prev => ({
            name: '',
            species: '',
            breed: '',
            birthDate: '',
            owner: { ownerId: prev.owner.ownerId }
          }));
        } else {
          return res.text().then(text => { throw new Error(text) });
        }
      })
      .catch(err => {
        console.error('Error al registrar mascota:', err);
        setSuccessMessage('Error al registrar mascota');
      });
  };

  if (loading) return <p>Cargando...</p>;

  return (
    <div>
      <h2>Registrar Nueva Mascota</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre:</label>
          <input name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Especie:</label>
          <input name="species" value={formData.species} onChange={handleChange} required />
        </div>
        <div>
          <label>Raza:</label>
          <input name="breed" value={formData.breed} onChange={handleChange} required />
        </div>
        <div>
          <label>Fecha de nacimiento:</label>
          <input type="date" name="birthDate" value={formData.birthDate} onChange={handleChange} required />
        </div>

        {/* Oculto el ownerId, pero está presente para enviar al backend */}
        <input type="hidden" name="ownerId" value={formData.owner.ownerId} />

        <button type="submit">Registrar</button>
      </form>

      {successMessage && <p>{successMessage}</p>}
    </div>
  );
}

export default PetForm;
