import React, { useState } from 'react';

function Registro() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    password: '',
    confirmPassword: '',
    termsAccepted: false,
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  
const handleSubmit = async (e) => {
  e.preventDefault();
  setError('');
  setSuccess('');

  if (formData.password !== formData.confirmPassword) {
    setError('Las contraseñas no coinciden');
    return;
  }
  if (!formData.termsAccepted) {
    setError('Debes aceptar los términos y condiciones');
    return;
  }

  const userToSend = {
    name: formData.name,
    email: formData.email,
    password: formData.password,
    phone: formData.phone,
    address: formData.address,
    role: 'duenio',
  };

  try {
    // Crear usuario
    const response = await fetch('http://localhost:8090/bd_petcare/api/users/save', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(userToSend),
    });

    if (!response.ok) {
      const errorText = await response.text();
      throw new Error(`Error al guardar el usuario: ${response.status} - ${errorText}`);
    }

    const createdUser = await response.json();
    console.log('Usuario creado:', createdUser);

  const duenioToSend = {
    phone: formData.phone,
    address: formData.address,
    user: {
        userId: createdUser.userId // Asegúrate de que esto coincida con la estructura esperada
    }
    };


    const duenioResponse = await fetch('http://localhost:8090/bd_petcare/api/owners/save', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(duenioToSend),
    });

    if (!duenioResponse.ok) {
      const errorText = await duenioResponse.text();
      throw new Error(`Error al guardar el dueño: ${duenioResponse.status} - ${errorText}`);
    }

    setSuccess('Usuario y dueño creados correctamente');
    setFormData({
      name: '',
      email: '',
      phone: '',
      address: '',
      password: '',
      confirmPassword: '',
      termsAccepted: false,
    });
    // Redirige a /login después de un pequeño retraso opcional (por ejemplo 1.5 segundos)
    setTimeout(() => {
    window.location.href = '/login';
    }, 250);

  } catch (err) {
    setError(err.message);
  }
};


  return (
    <div className="registerpet-root">
      <div className="container registerpet-container">
        <div className="row justify-content-center">
          <div className="col-lg-8">
            <div className="registerpet-form-container">
              <div className="registerpet-header">
                <img
                  src="https://images.unsplash.com/photo-1586671267731-da2cf3ceeb80?ixlib=rb-1.2.1&auto=format&fit=crop&w=200&h=200&q=80"
                  alt="Logo PetCare"
                  className="registerpet-logo"
                />
                <h4 className="registerpet-text-secondary mb-3">Únete a PetCare</h4>
                <h1 className="registerpet-title">
                  <span className="registerpet-text-primary">Crear</span>{' '}
                  <span className="registerpet-text-secondary">Cuenta</span>
                </h1>
                <p className="registerpet-subtitle">Regístrate para acceder a todos nuestros servicios</p>
              </div>

              <form onSubmit={handleSubmit}>
                <div className="registerpet-form-group">
                  <h5 className="registerpet-text-primary mb-3">
                    <i className="fas fa-user-circle me-2"></i> Información Personal
                  </h5>

                  <div className="registerpet-form-group">
                    <label htmlFor="name" className="registerpet-form-label">
                      Nombre Completo
                    </label>
                    <div className="registerpet-input-group">
                      <i className="fas fa-user registerpet-input-icon"></i>
                      <input
                        type="text"
                        className="registerpet-form-control"
                        id="name"
                        name="name"
                        placeholder="Ingresa tu nombre completo"
                        value={formData.name}
                        onChange={handleChange}
                        required
                      />
                    </div>
                  </div>
                </div>

                <div className="registerpet-form-group">
                  <h5 className="registerpet-text-primary mb-3">
                    <i className="fas fa-address-card me-2"></i> Información de Contacto
                  </h5>

                  <div className="registerpet-form-group">
                    <label htmlFor="email" className="registerpet-form-label">
                      Correo Electrónico
                    </label>
                    <div className="registerpet-input-group">
                      <i className="fas fa-envelope registerpet-input-icon"></i>
                      <input
                        type="email"
                        className="registerpet-form-control"
                        id="email"
                        name="email"
                        placeholder="ejemplo@petcare.com"
                        value={formData.email}
                        onChange={handleChange}
                        required
                      />
                    </div>
                  </div>

                  <div className="registerpet-row">
                    <div className="registerpet-col">
                      <label htmlFor="phone" className="registerpet-form-label">
                        Teléfono
                      </label>
                      <div className="registerpet-input-group">
                        <i className="fas fa-phone registerpet-input-icon"></i>
                        <input
                          type="tel"
                          className="registerpet-form-control"
                          id="phone"
                          name="phone"
                          placeholder="Número de contacto"
                          value={formData.phone}
                          onChange={handleChange}
                        />
                      </div>
                    </div>

                    <div className="registerpet-col">
                      <label htmlFor="address" className="registerpet-form-label">
                        Dirección
                      </label>
                      <div className="registerpet-input-group">
                        <i className="fas fa-home registerpet-input-icon"></i>
                        <input
                          type="text"
                          className="registerpet-form-control"
                          id="address"
                          name="address"
                          placeholder="Dirección de residencia"
                          value={formData.address}
                          onChange={handleChange}
                        />
                      </div>
                    </div>
                  </div>
                </div>

                <div className="registerpet-form-group">
                  <h5 className="registerpet-text-primary mb-3">
                    <i className="fas fa-lock me-2"></i> Seguridad
                  </h5>

                  <div className="registerpet-row">
                    <div className="registerpet-col">
                      <label htmlFor="password" className="registerpet-form-label">
                        Contraseña
                      </label>
                      <div className="registerpet-input-group">
                        <i className="fas fa-key registerpet-input-icon"></i>
                        <input
                          type="password"
                          className="registerpet-form-control"
                          id="password"
                          name="password"
                          placeholder="••••••••"
                          value={formData.password}
                          onChange={handleChange}
                          required
                        />
                      </div>
                    </div>

                    <div className="registerpet-col">
                      <label htmlFor="confirmPassword" className="registerpet-form-label">
                        Confirmar Contraseña
                      </label>
                      <div className="registerpet-input-group">
                        <i className="fas fa-check-circle registerpet-input-icon"></i>
                        <input
                          type="password"
                          className="registerpet-form-control"
                          id="confirmPassword"
                          name="confirmPassword"
                          placeholder="••••••••"
                          value={formData.confirmPassword}
                          onChange={handleChange}
                          required
                        />
                      </div>
                    </div>
                  </div>
                </div>

                <div className="registerpet-form-group">
                  <div className="form-check">
                    <input
                      className="form-check-input"
                      type="checkbox"
                      id="termsAccepted"
                      name="termsAccepted"
                      checked={formData.termsAccepted}
                      onChange={handleChange}
                      required
                    />
                    <label className="form-check-label registerpet-text-muted" htmlFor="termsAccepted">
                      Acepto los{' '}
                      <a href="/" className="registerpet-link">
                        Términos y Condiciones
                      </a>{' '}
                      y la{' '}
                      <a href="/" className="registerpet-link">
                        Política de Privacidad
                      </a>
                    </label>
                  </div>
                </div>

                {error && <p style={{ color: 'red' }}>{error}</p>}
                {success && <p style={{ color: 'green' }}>{success}</p>}

                <button type="submit" className="registerpet-btn registerpet-btn-primary mb-3">
                  <i className="fas fa-user-plus me-2"></i> Registrarse
                </button>

                <div className="registerpet-divider">o</div>

                <div className="text-center">
                  <p className="registerpet-text-muted">
                    ¿Ya tienes una cuenta? <a href="/login" className="registerpet-link">Iniciar Sesión</a>
                  </p>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Registro;
