import React, { useCallback, useEffect, useState } from 'react';
import UserTable from '../componentes/UserTable';
import OwnerTable from '../componentes/OwnerTable';
import PetTable from '../componentes/PetTable';
import { useNavigate } from 'react-router-dom';
import './dashboard.css';
import {
  FaUser, FaPaw, FaUsersCog, FaSignOutAlt, FaSearch,
  FaUserMd, FaUserNurse
} from 'react-icons/fa';
import RegistroVeterinario from './registroVeterinario';
import RegistroAsistente from './registroAsistente';

const USER_API = 'http://localhost:8090/bd_petcare/api/users';
const OWNER_API = 'http://localhost:8090/bd_petcare/api/owners';
const PET_API = 'http://localhost:8090/bd_petcare/api/pets';

function Dashboard() {
  const [users, setUsers] = useState([]);
  const [owners, setOwners] = useState([]);
  const [pets, setPets] = useState([]);
  const [usuario, setUsuario] = useState(null);
  const [section, setSection] = useState('r_vets');
  const [showActions, setShowActions] = useState(false);

  const [inputValue, setInputValue] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  const navigate = useNavigate();

  const fetchAll = useCallback(() => {
    fetchUsers();
    fetchOwners();
    fetchPets();
  }, []);

  useEffect(() => {
    const usuarioGuardado = localStorage.getItem('usuario');
    if (usuarioGuardado) {
      setUsuario(JSON.parse(usuarioGuardado));
    } else {
      navigate("/login");
    }
    fetchAll();
  }, [fetchAll, navigate]);

  const fetchUsers = () => {
    fetch(`${USER_API}/all`)
      .then(res => res.json())
      .then(setUsers)
      .catch(err => console.error('Error cargando usuarios:', err));
  };

  const fetchOwners = () => {
    fetch(`${OWNER_API}/all`)
      .then(res => res.json())
      .then(setOwners)
      .catch(err => console.error('Error cargando dueños:', err));
  };

  const fetchPets = () => {
    fetch(`${PET_API}/all`)
      .then(res => res.json())
      .then(setPets)
      .catch(err => console.error('Error cargando mascotas:', err));
  };

  const handleUserDelete = (id) => {
    fetch(`${USER_API}/delete/${id}`, { method: 'DELETE' })
      .then(fetchAll)
      .catch(err => console.error('Error al eliminar usuario:', err));
  };

  const handleUserUpdate = (user) => {
    fetch(`${USER_API}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(user)
    })
      .then(fetchUsers)
      .catch(err => console.error('Error al actualizar usuario:', err));
  };

  const handleOwnerUpdate = (owner) => {
    fetch(`${OWNER_API}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(owner)
    })
      .then(fetchOwners)
      .catch(err => console.error('Error al actualizar dueño:', err));
  };

  const handlePetDelete = (id) => {
    fetch(`${PET_API}/delete/${id}`, { method: 'DELETE' })
      .then(fetchPets)
      .catch(err => console.error('Error al eliminar mascota:', err));
  };

  const handlePetUpdate = (pet) => {
    fetch(`${PET_API}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(pet)
    })
      .then(fetchPets)
      .catch(err => console.error('Error al actualizar mascota:', err));
  };

  const cerrarSesion = () => {
    localStorage.removeItem('usuario');
    navigate("/login");
  };

  const filterData = (data) => {
    if (!searchTerm) return data;
    return data.filter(item =>
      (item.name && item.name.toLowerCase().includes(searchTerm.toLowerCase())) ||
      (item.phone && item.phone.includes(searchTerm))
    );
  };

  const handleSearchClick = () => {
    setSearchTerm(inputValue.trim());
  };

  const handleEnterKey = (e) => {
    if (e.key === 'Enter') {
      handleSearchClick();
    }
  };

  const handleRegistroExitoso = () => {
  fetchAll(); // o también window.location.reload();
};


  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1><FaUsersCog /> PetCare Dashboard</h1>
        <div className="user-info">
          <FaUser />
          <span>Hola, {usuario?.name}</span>
          <div className="acciones-wrapper">
            <button onClick={() => setShowActions(prev => !prev)}>
              <FaSignOutAlt /> Acciones
            </button>
            {showActions && (
              <div className="acciones-menu">
                <button onClick={() => navigate("/login")}>Login</button>
                <button onClick={cerrarSesion}>Cerrar sesión</button>
              </div>
            )}
          </div>
        </div>
      </header>

      <div className="dashboard-body">
        <aside className="sidebar">
          <button
            className={section === 'r_vets' ? 'active' : ''}
            onClick={() => setSection('r_vets')}
          >
            <FaUserMd /> Registrar Veterinarios
          </button>
          <button
            className={section === 'r_asists' ? 'active' : ''}
            onClick={() => setSection('r_asists')}
          >
            <FaUserNurse /> Registrar Asistentes
          </button>
          <button
            className={section === 'users' ? 'active' : ''}
            onClick={() => setSection('users')}
          >
            <FaUsersCog /> Usuarios
          </button>
          <button
            className={section === 'owners' ? 'active' : ''}
            onClick={() => setSection('owners')}
          >
            <FaUser /> Dueños
          </button>
          <button
            className={section === 'pets' ? 'active' : ''}
            onClick={() => setSection('pets')}
          >
            <FaPaw /> Mascotas
          </button>
        </aside>

        <main className="dashboard-content">
          <div className="search-wrapper">
            <button className="search-btn" onClick={handleSearchClick} aria-label="Buscar">
              <FaSearch />
            </button>
            <input
              type="text"
              placeholder="Buscar por nombre o dato que necesites"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              onKeyDown={handleEnterKey}
              className="search-input"
            />
          </div>

          {section === 'users' && (
            <>
              <h2>Gestión de Usuarios</h2>
              <UserTable users={filterData(users)} onDelete={handleUserDelete} onUpdate={handleUserUpdate} />
            </>
          )}

        {section === 'r_vets' && <RegistroVeterinario onSuccess={handleRegistroExitoso} />}
        {section === 'r_asists' && <RegistroAsistente onSuccess={handleRegistroExitoso} />}

          {section === 'owners' && (
            <>
              <h2>Gestión de Dueños</h2>
              <OwnerTable owners={filterData(owners)} onUpdate={handleOwnerUpdate} />
            </>
          )}

          {section === 'pets' && (
            <>
              <h2>Gestión de Mascotas</h2>
              <PetTable pets={filterData(pets)} onUpdate={handlePetUpdate} onDelete={handlePetDelete} />
            </>
          )}
        </main>
      </div>
    </div>
  );
}

export default Dashboard;
