import React, { useState, useEffect } from 'react';

const PET_API = 'http://localhost:8090/bd_petcare/api/pets';
const OWNER_API = 'http://localhost:8090/bd_petcare/api/owners';

function PetForm() {
  const [formData, setFormData] = useState({
    name: '',
    species: '',
    breed: '',
    birthDate: '',
    owner: {
      ownerId: ''
    }
  });

  const [successMessage, setSuccessMessage] = useState('');
  const [loadingOwner, setLoadingOwner] = useState(true);

  useEffect(() => {
    const usuario = JSON.parse(localStorage.getItem('usuario'));

    if (usuario) {
      const userId = usuario.userId;

      // Buscar dueño por userId
      fetch(`${OWNER_API}/findByUser/${userId}`)
        .then(res => {
          if (!res.ok) throw new Error("Dueño no encontrado");
          return res.json();
        })
        .then(data => {
          setFormData(prev => ({
            ...prev,
            owner: { ownerId: data.ownerId }
          }));
        })
        .catch(err => {
          console.error("Error buscando dueño:", err);
          setSuccessMessage("Error: no se pudo encontrar el dueño asociado.");
        })
        .finally(() => setLoadingOwner(false));
    } else {
      setSuccessMessage("Usuario no logueado.");
      setLoadingOwner(false);
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch(`${PET_API}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
      .then(res => {
        if (res.ok) {
          setSuccessMessage('Mascota registrada correctamente');
          setFormData(prev => ({
            ...prev,
            name: '',
            species: '',
            breed: '',
            birthDate: ''
          }));
        } else {
          return res.text().then(text => { throw new Error(text) });
        }
      })
      .catch(err => {
        console.error('Error al registrar mascota:', err);
        setSuccessMessage('Error al registrar mascota');
      });
  };

  if (loadingOwner) {
    return <p>Cargando dueño asociado...</p>;
  }

  return (
    <div>
      <h2>Registrar Nueva Mascota</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre:</label>
          <input name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Especie:</label>
          <input name="species" value={formData.species} onChange={handleChange} required />
        </div>
        <div>
          <label>Raza:</label>
          <input name="breed" value={formData.breed} onChange={handleChange} required />
        </div>
        <div>
          <label>Fecha de nacimiento:</label>
          <input type="date" name="birthDate" value={formData.birthDate} onChange={handleChange} required />
        </div>

        <button type="submit">Registrar</button>
      </form>

      {successMessage && <p>{successMessage}</p>}
    </div>
  );
}

export default PetForm;
