import React, { useState, useEffect } from 'react';

const PET_API = 'http://localhost:8090/bd_petcare/api/pets';
const OWNER_BY_USER_API = 'http://localhost:8090/bd_petcare/api/owners/user'; // + /{userId}

function PetForm() {
  const [formData, setFormData] = useState({
    name: '',
    species: '',
    breed: '',
    birthDate: '',
    owner: { ownerId: '' }
  });

  const [successMessage, setSuccessMessage] = useState('');
  const [usuario, setUsuario] = useState(null);
  const [loadingOwner, setLoadingOwner] = useState(true);

  useEffect(() => {
    const usuarioGuardado = localStorage.getItem('usuario');
    if (usuarioGuardado) {
      const user = JSON.parse(usuarioGuardado);
      setUsuario(user);

      if (user.role === 'duenio') {
        // Solo si es dueño, traer ownerId
        fetch(`${OWNER_BY_USER_API}/${user.userId}`)
          .then(res => {
            if (!res.ok) throw new Error('No se pudo obtener el dueño');
            return res.json();
          })
          .then(owner => {
            setFormData(prev => ({
              ...prev,
              owner: { ownerId: owner.ownerId }
            }));
          })
          .catch(err => {
            console.error('Error obteniendo ownerId:', err);
            setSuccessMessage('Error al cargar datos del dueño');
          })
          .finally(() => setLoadingOwner(false));
      } else {
        // Para otros roles no hay ownerId
        setLoadingOwner(false);
      }
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch(`${PET_API}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
      .then(res => {
        if (res.ok) {
          setSuccessMessage('Mascota registrada correctamente');
          setFormData({
            name: '',
            species: '',
            breed: '',
            birthDate: '',
            owner: { ownerId: formData.owner.ownerId }
          });
        } else {
          return res.text().then(text => { throw new Error(text); });
        }
      })
      .catch(err => {
        console.error('Error al registrar mascota:', err);
        setSuccessMessage('Error al registrar mascota');
      });
  };

  // --- Renderizado condicional por rol ---
  if (!usuario) {
    return <p>Cargando usuario...</p>;
  }

  if (loadingOwner) {
    return <p>Cargando datos...</p>;
  }

  if (usuario.role === 'duenio') {
    // Mostrar formulario para dueño
    return (
      <div>
        <h2>Registrar Nueva Mascota</h2>
        <form onSubmit={handleSubmit}>
          <div>
            <label>Nombre:</label>
            <input name="name" value={formData.name} onChange={handleChange} required />
          </div>
          <div>
            <label>Especie:</label>
            <input name="species" value={formData.species} onChange={handleChange} required />
          </div>
          <div>
            <label>Raza:</label>
            <input name="breed" value={formData.breed} onChange={handleChange} required />
          </div>
          <div>
            <label>Fecha de nacimiento:</label>
            <input type="date" name="birthDate" value={formData.birthDate} onChange={handleChange} required />
          </div>
          <p>Registrando como dueño: {usuario.userId} - {usuario.name}</p>
          <button type="submit" disabled={!formData.owner.ownerId}>
            Registrar
          </button>
        </form>
        {successMessage && <p>{successMessage}</p>}
      </div>
    );
  } else if (usuario.role === 'asistente') {
    // Mostrar lista o mensaje para asistentes
    return (
      <div>
        <h2>Bienvenido, Asistente</h2>
        <p>Aquí podrías ver las mascotas asignadas para asistencia o tareas.</p>
        {/* Aquí puedes agregar una lista u otra funcionalidad para asistentes */}
      </div>
    );
  } else if (usuario.role === 'veterinario') {
    // Mostrar lista o mensaje para veterinarios
    return (
      <div>
        <h2>Bienvenido, Veterinario</h2>
        <p>Aquí podrías ver las mascotas a las que das seguimiento.</p>
        {/* Aquí puedes agregar lista u otra funcionalidad para veterinarios */}
      </div>
    );
  } else {
    // Roles desconocidos o sin permisos
    return <p>No tienes permiso para registrar mascotas.</p>;
  }
}

export default PetForm;
