import React, { useEffect, useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Dialog, DialogContent, DialogTrigger } from "@/components/ui/dialog";

export default function DashboardUsuarios() {
  const [usuarios, setUsuarios] = useState([]);
  const [usuarioEditando, setUsuarioEditando] = useState(null);
  const [formData, setFormData] = useState({ nombre: "", correo: "" });

  useEffect(() => {
    fetch("http://localhost:8090/bd_petcare/api/users")
      .then((res) => res.json())
      .then((data) => setUsuarios(data));
  }, []);

  const eliminarUsuario = (id) => {
    fetch(`http://localhost:8080/api/usuarios/${id}`, {
      method: "DELETE",
    })
      .then(() => setUsuarios(usuarios.filter((u) => u.id !== id)))
      .catch((err) => console.error(err));
  };

  const abrirEditor = (usuario) => {
    setUsuarioEditando(usuario);
    setFormData({ nombre: usuario.nombre, correo: usuario.correo });
  };

  const guardarCambios = () => {
    fetch(`http://localhost:8080/api/usuarios/${usuarioEditando.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    })
      .then(() => {
        setUsuarios((prev) =>
          prev.map((u) =>
            u.id === usuarioEditando.id ? { ...u, ...formData } : u
          )
        );
        setUsuarioEditando(null);
      })
      .catch((err) => console.error(err));
  };

  return (
    <div className="p-4 grid gap-4 grid-cols-1 md:grid-cols-2 lg:grid-cols-3">
      {usuarios.map((usuario) => (
        <Card key={usuario.id} className="shadow-xl">
          <CardContent className="p-4">
            <p className="font-bold">ID: {usuario.id}</p>
            <p>Nombre: {usuario.nombre}</p>
            <p>Correo: {usuario.correo}</p>
            <div className="flex gap-2 mt-4">
              <Dialog>
                <DialogTrigger asChild>
                  <Button onClick={() => abrirEditor(usuario)} variant="outline">
                    Editar
                  </Button>
                </DialogTrigger>
                <DialogContent className="space-y-4">
                  <h2 className="text-lg font-bold">Editar Usuario (ID: {usuarioEditando?.id})</h2>
                  <Input
                    placeholder="Nombre"
                    value={formData.nombre}
                    onChange={(e) =>
                      setFormData({ ...formData, nombre: e.target.value })
                    }
                  />
                  <Input
                    placeholder="Correo"
                    value={formData.correo}
                    onChange={(e) =>
                      setFormData({ ...formData, correo: e.target.value })
                    }
                  />
                  <Button onClick={guardarCambios}>Guardar</Button>
                </DialogContent>
              </Dialog>
              <Button
                onClick={() => eliminarUsuario(usuario.id)}
                variant="destructive"
              >
                Eliminar
              </Button>
            </div>
          </CardContent>
        </Card>
      ))}
    </div>
  );
}
