import React, { useState, useEffect } from 'react';

const PET_API = 'http://localhost:8090/bd_petcare/api/pets';
const OWNER_BY_USER_API = 'http://localhost:8090/bd_petcare/api/owners/user'; // + /{userId}

const styles = {
  container: {
    maxWidth: '600px',
    margin: '40px auto',
    backgroundColor: '#fff',
    borderRadius: '12px',
    boxShadow: '0 8px 24px rgba(0,0,0,0.1)',
    padding: '30px',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  header: {
    textAlign: 'center',
    marginBottom: '24px',
  },
  title: {
    fontSize: '2rem',
    marginBottom: '8px',
    color: '#2c3e50',
  },
  subtitle: {
    fontSize: '1rem',
    color: '#7f8c8d',
  },
  formGroup: {
    marginBottom: '20px',
  },
  label: {
    display: 'block',
    marginBottom: '6px',
    fontWeight: '600',
    color: '#34495e',
  },
  input: {
    width: '100%',
    padding: '10px 14px',
    borderRadius: '6px',
    border: '1.8px solid #bdc3c7',
    fontSize: '1rem',
    outline: 'none',
    transition: 'border-color 0.3s',
  },
  inputFocus: {
    borderColor: '#2980b9',
  },
  select: {
    width: '100%',
    padding: '10px 14px',
    borderRadius: '6px',
    border: '1.8px solid #bdc3c7',
    fontSize: '1rem',
    outline: 'none',
    backgroundColor: '#fff',
  },
  button: {
    width: '100%',
    backgroundColor: '#2980b9',
    color: '#fff',
    padding: '14px',
    fontSize: '1.1rem',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    fontWeight: '700',
    marginTop: '12px',
    transition: 'background-color 0.3s',
  },
  buttonDisabled: {
    backgroundColor: '#95a5a6',
    cursor: 'not-allowed',
  },
  successMsg: {
    marginTop: '15px',
    color: '#27ae60',
    fontWeight: '600',
    textAlign: 'center',
  },
  imagePreview: {
    display: 'block',
    maxWidth: '180px',
    margin: '10px auto 20px',
    borderRadius: '12px',
    boxShadow: '0 4px 16px rgba(0,0,0,0.15)',
  },
  userInfo: {
    marginTop: '20px',
    fontStyle: 'italic',
    color: '#34495e',
    textAlign: 'center',
  }
};

function PetForm() {
  const [formData, setFormData] = useState({
    name: '',
    species: '',
    breed: '',
    birthDate: '',
    owner: { ownerId: '' },
    photo: null,
  });

  const [successMessage, setSuccessMessage] = useState('');
  const [usuario, setUsuario] = useState(null);
  const [loadingOwner, setLoadingOwner] = useState(true);
  const [imagePreview, setImagePreview] = useState(null);

  useEffect(() => {
    const usuarioGuardado = localStorage.getItem('usuario');
    if (usuarioGuardado) {
      const user = JSON.parse(usuarioGuardado);
      setUsuario(user);

      if (user.role === 'duenio') {
        fetch(`${OWNER_BY_USER_API}/${user.userId}`)
          .then(res => {
            if (!res.ok) throw new Error('No se pudo obtener el dueño');
            return res.json();
          })
          .then(owner => {
            setFormData(prev => ({
              ...prev,
              owner: { ownerId: owner.ownerId }
            }));
          })
          .catch(err => {
            console.error('Error obteniendo ownerId:', err);
            setSuccessMessage('Error al cargar datos del dueño');
          })
          .finally(() => setLoadingOwner(false));
      } else {
        setLoadingOwner(false);
      }
    }
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    if (name === 'photo' && files.length > 0) {
      setFormData(prev => ({ ...prev, photo: files[0] }));
      setImagePreview(URL.createObjectURL(files[0]));
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Construir formData para enviar archivo si quieres implementar foto subida
    // Por ahora envio JSON normal (sin foto)
    fetch(`${PET_API}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    })
      .then(res => {
        if (res.ok) {
          setSuccessMessage('Mascota registrada correctamente');
          setFormData(prev => ({
            name: '',
            species: '',
            breed: '',
            birthDate: '',
            owner: { ownerId: prev.owner.ownerId },
            photo: null,
          }));
          setImagePreview(null);
        } else {
          return res.text().then(text => { throw new Error(text); });
        }
      })
      .catch(err => {
        console.error('Error al registrar mascota:', err);
        setSuccessMessage('Error al registrar mascota');
      });
  };

  if (!usuario) return <p style={{ textAlign: 'center' }}>Cargando usuario...</p>;
  if (loadingOwner) return <p style={{ textAlign: 'center' }}>Cargando datos...</p>;

  if (usuario.role !== 'duenio') {
    return (
      <div style={{ textAlign: 'center', marginTop: '40px', color: '#7f8c8d' }}>
        <h2>Bienvenido, {usuario.role.charAt(0).toUpperCase() + usuario.role.slice(1)}</h2>
        <p>Aquí puedes ver tus asignaciones y tareas.</p>
      </div>
    );
  }

  return (
    <div style={styles.container}>

      <div style={styles.header}>
        <h1 style={styles.title}>Registra a tu Mascota</h1>
        <p style={styles.subtitle}>Completa los datos para brindarle el mejor cuidado</p>
      </div>

      <form onSubmit={handleSubmit}>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="name">Nombre de la mascota</label>
          <input
            type="text"
            id="name"
            name="name"
            style={styles.input}
            placeholder="Ej: Max"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="species">Especie</label>
          <select
            id="species"
            name="species"
            style={styles.select}
            value={formData.species}
            onChange={handleChange}
            required
          >
            <option value="" disabled>Selecciona una especie</option>
            <option value="dog">Perro</option>
            <option value="cat">Gato</option>
            <option value="bird">Ave</option>
            <option value="rabbit">Conejo</option>
            <option value="other">Otra</option>
          </select>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="breed">Raza</label>
          <input
            type="text"
            id="breed"
            name="breed"
            style={styles.input}
            placeholder="Ej: Golden Retriever"
            value={formData.breed}
            onChange={handleChange}
            required
          />
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="birthDate">Fecha de nacimiento</label>
          <input
            type="date"
            id="birthDate"
            name="birthDate"
            style={styles.input}
            value={formData.birthDate}
            onChange={handleChange}
            required
          />
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="photo">Foto de tu Mascota (Opcional)</label>
          <input
            type="file"
            id="photo"
            name="photo"
            accept="image/*"
            onChange={handleChange}
            style={{ ...styles.input, padding: '6px 14px' }}
          />
          {imagePreview && (
            <img
              src={imagePreview}
              alt="Vista previa de mascota"
              style={styles.imagePreview}
            />
          )}
        </div>

        <p style={styles.userInfo}>
          Registrando como dueño: <strong>{usuario.name}</strong> - <em>{usuario.email}</em>
        </p>

        <button
          type="submit"
          style={formData.owner.ownerId ? styles.button : { ...styles.button, ...styles.buttonDisabled }}
          disabled={!formData.owner.ownerId}
        >
          Registrar Mascota
        </button>
      </form>

      {successMessage && <p style={styles.successMsg}>{successMessage}</p>}
    </div>
  );
}

export default PetForm;
