// src/components/Testimonios.js
import React from 'react';
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';

const opciones = {
  center: true,
  autoplay: true,
  smartSpeed: 2000,
  dots: true,
  loop: true,
  responsive: {
    0: { items: 1 },
    576: { items: 1 },
    768: { items: 2 },
    992: { items: 3 }
  }
};

const datosTestimonios = [
  {
    nombre: 'María López',
    profesion: 'Veterinaria',
    mensaje: 'El servicio es excelente, siempre atienden con mucha dedicación y cariño a nuestras mascotas. ¡Altamente recomendados!',
    imagen: 'img/testimonial-1.jpg'
  },
  {
    nombre: 'Carlos Ramírez',
    profesion: 'Ingeniero',
    mensaje: 'Llevo a mi perro aquí desde hace años. Siempre he recibido atención de calidad y muy profesional.',
    imagen: 'img/testimonial-2.jpg'
  },
  {
    nombre: 'Lucía Fernández',
    profesion: 'Docente',
    mensaje: 'El equipo de PetCare es muy amable y atento. Mi gato siempre está tranquilo cuando lo llevo.',
    imagen: 'img/testimonial-3.jpg'
  },
  {
    nombre: 'Pedro Soto',
    profesion: 'Empresario',
    mensaje: 'Un lugar limpio, ordenado y con un personal que realmente ama a los animales. Muy recomendable.',
    imagen: 'img/testimonial-4.jpg'
  }
];

function Testimonios() {
  return (
    <div className="container-fluid bg-light my-5 p-0 py-5">
      <div className="container p-0 py-5">
        <div className="d-flex flex-column text-center mb-5">
          <h4 className="text-secondary mb-3">Testimonios</h4>
          <h1 className="display-4 m-0">
            Nuestros clientes <span className="text-primary">dicen</span>
          </h1>
        </div>

        <OwlCarousel className="owl-theme testimonial-carousel" {...opciones}>
          {datosTestimonios.map((t, i) => (
            <div className="bg-white mx-3 p-4" key={i}>
              <div className="d-flex align-items-end mb-3 mt-n4 ml-n4">
                <img className="img-fluid" src={t.imagen} alt={t.nombre} style={{ width: 80, height: 80 }} />
                <div className="ml-3">
                  <h5>{t.nombre}</h5>
                  <i>{t.profesion}</i>
                </div>
              </div>
              <p className="m-0">{t.mensaje}</p>
            </div>
          ))}
        </OwlCarousel>
      </div>
    </div>
  );
}

export default Testimonios;
