import React, { useEffect, useState } from 'react';
import UserTable from '../componentes/UserTable';
import OwnerTable from '../componentes/OwnerTable';
import PetTable from '../componentes/PetTable';
import './dashboard-styles.css'; // Aquí irán nuestros estilos únicos

const USER_API = 'http://localhost:8090/bd_petcare/api/users';
const OWNER_API = 'http://localhost:8090/bd_petcare/api/owners';
const PET_API = 'http://localhost:8090/bd_petcare/api/pets';

function Dashboard() {
  const [activeTab, setActiveTab] = useState('usuarios');
  const [users, setUsers] = useState([]);
  const [owners, setOwners] = useState([]);
  const [pets, setPets] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);

  const fetchUsers = () => {
    fetch(`${USER_API}/all`)
      .then(res => res.json())
      .then(setUsers)
      .catch(err => console.error('Error cargando usuarios:', err));
  };

  const fetchOwners = () => {
    fetch(`${OWNER_API}/all`)
      .then(res => res.json())
      .then(setOwners)
      .catch(err => console.error('Error cargando dueños:', err));
  };

  const fetchPets = () => {
    fetch(`${PET_API}/all`)
      .then(res => res.json())
      .then(setPets)
      .catch(err => console.error('Error cargando mascotas:', err));
  };

  useEffect(() => {
    fetchUsers();
    fetchOwners();
    fetchPets();
  }, []);

  const handleUserDelete = (id) => {
    fetch(`${USER_API}/delete/${id}`, { method: 'DELETE' })
      .then(() => {
        fetchUsers();
        fetchOwners();
        fetchPets();
      })
      .catch(err => console.error('Error al eliminar usuario:', err));
  };

  const handleUserUpdate = (user) => {
    fetch(`${USER_API}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(user)
    })
      .then(() => {
        fetchUsers();
        setSelectedUser(user); // mostrar arriba el usuario actualizado
      })
      .catch(err => console.error('Error al actualizar usuario:', err));
  };

  const handleOwnerUpdate = (owner) => {
    fetch(`${OWNER_API}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(owner)
    })
      .then(() => fetchOwners())
      .catch(err => console.error('Error al actualizar dueño:', err));
  };

  const handlePetDelete = (id) => {
    fetch(`${PET_API}/delete/${id}`, { method: 'DELETE' })
      .then(() => fetchPets())
      .catch(err => console.error('Error al eliminar mascota:', err));
  };

  const handlePetUpdate = (pet) => {
    fetch(`${PET_API}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(pet)
    })
      .then(() => fetchPets())
      .catch(err => console.error('Error al actualizar mascota:', err));
  };

  return (
    <div className="dashboard-container">
                        <span style={{ fontFamily: 'Nunito Sans', fontSize: '1 rem' }}>
                  Hola, {usuario.name}
                </span>
      <aside className="dashboard-sidebar">
        <button onClick={() => setActiveTab('usuarios')}>Usuarios</button>
        <button onClick={() => setActiveTab('duenos')}>Dueños</button>
        <button onClick={() => setActiveTab('mascotas')}>Mascotas</button>
      </aside>

      <main className="dashboard-main">
        <h1 className="dashboard-title">Panel de Administración</h1>

        {activeTab === 'usuarios' && (
          <>
            {selectedUser && (
              <div className="dashboard-selected">
                Usuario activo: <strong>{selectedUser.name} (ID: {selectedUser.userId})</strong>
              </div>
            )}
            <UserTable
              users={users}
              onDelete={handleUserDelete}
              onUpdate={handleUserUpdate}
              setSelectedUser={setSelectedUser} // lo pasamos para mostrarlo arriba
            />
          </>
        )}

        {activeTab === 'duenos' && (
          <>
            <OwnerTable owners={owners} onUpdate={handleOwnerUpdate} />
          </>
        )}

        {activeTab === 'mascotas' && (
          <>
            <PetTable pets={pets} onUpdate={handlePetUpdate} onDelete={handlePetDelete} />
          </>
        )}
      </main>
    </div>
  );
}

export default Dashboard;
