import React, { useState, useEffect } from 'react';

import './login.css'; // Archivo CSS para estilos personalizados

function Login() {
  const [usuarios, setUsuarios] = useState([]);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Simulamos un pequeño delay para la animación de carga
    const timer = setTimeout(() => {
      fetch("http://localhost:8090/bd_petcare/api/users/all")
        .then(res => res.json())
        .then(data => {
          setUsuarios(data);
          setIsLoading(false);
        })
        .catch(err => {
          console.error("Error al obtener usuarios", err);
          setIsLoading(false);
        });
    }, 1500);

    return () => clearTimeout(timer);
  }, []);

  const manejarLogin = (e) => {
    e.preventDefault();
    setIsLoading(true);

    // Pequeño delay para la animación del botón
    setTimeout(() => {
      const usuarioEncontrado = usuarios.find(u => u.email === email);

      if (!usuarioEncontrado) {
        setMensaje("Correo no encontrado");
        setIsLoading(false);
        return;
      }

      if (usuarioEncontrado.password !== password) {
        setMensaje("Contraseña incorrecta");
        setIsLoading(false);
        return;
      }

      // Guardar el usuario en localStorage
      localStorage.setItem('usuario', JSON.stringify(usuarioEncontrado));

      // Determinar la ruta de redirección basada en el rol y userId
      const { role, userId } = usuarioEncontrado;
      let rutaRedireccion = '/';

      if (role === 'asistente') {
        rutaRedireccion = '/registrarMascotas';
      } else if (role === 'veterinario') {
        rutaRedireccion = userId === 1 ? '/admin' : '/registrarMascotas';
      }

      // Redirigir a la ruta correspondiente
      window.location.replace(rutaRedireccion);
    }, 800);
  };

  // Animaciones
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        when: "beforeChildren",
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        type: "spring",
        stiffness: 100
      }
    }
  };

  const imageVariants = {
    hidden: { x: 50, opacity: 0 },
    visible: {
      x: 0,
      opacity: 1,
      transition: {
        delay: 0.5,
        type: "spring",
        stiffness: 50
      }
    }
  };

  return (
    <motion.div 
      className="loginpet-root"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
    >
      <div className="container loginpet-container">
        <div className="row justify-content-center">
          {/* Formulario de Login */}
          <motion.div 
            className="col-lg-5 pe-lg-4"
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            <motion.div 
              className="loginpet-form-container"
              variants={itemVariants}
            >
              <motion.div 
                className="loginpet-header"
                variants={itemVariants}
              >
                <motion.img 
                  src="https://images.unsplash.com/photo-1586671267731-da2cf3ceeb80?ixlib=rb-1.2.1&auto=format&fit=crop&w=200&h=200&q=80"
                  alt="Logo PetCare"
                  className="loginpet-logo"
                  whileHover={{ rotate: 10, scale: 1.05 }}
                  transition={{ type: "spring", stiffness: 300 }}
                />
                <h4 className="loginpet-text-secondary mb-3">Bienvenido a PetCare</h4>
                <h1 className="loginpet-title">
                  <span className="loginpet-text-primary">Iniciar</span>{' '}
                  <span className="loginpet-text-secondary">Sesión</span>
                </h1>
                <p className="loginpet-subtitle">Ingresa para administrar los servicios de tu mascota</p>
              </motion.div>

              <form onSubmit={manejarLogin}>
                <motion.div 
                  className="loginpet-form-group"
                  variants={itemVariants}
                >
                  <label className="loginpet-form-label">Correo Electrónico</label>
                  <div className="loginpet-input-group">
                    <i className="fas fa-envelope loginpet-input-icon"></i>
                    <input
                      type="email"
                      className="loginpet-form-control"
                      placeholder="ejemplo@petcare.com"
                      required
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                    />
                  </div>
                </motion.div>

                <motion.div 
                  className="loginpet-form-group"
                  variants={itemVariants}
                >
                  <label className="loginpet-form-label">Contraseña</label>
                  <div className="loginpet-input-group">
                    <i className="fas fa-lock loginpet-input-icon"></i>
                    <input
                      type="password"
                      className="loginpet-form-control"
                      placeholder="••••••••"
                      required
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                    />
                  </div>
                </motion.div>

                {mensaje && (
                  <motion.div 
                    className="alert alert-danger mt-2"
                    initial={{ scale: 0.9, opacity: 0 }}
                    animate={{ scale: 1, opacity: 1 }}
                    exit={{ scale: 0.9, opacity: 0 }}
                  >
                    {mensaje}
                  </motion.div>
                )}

                <motion.div variants={itemVariants}>
                  <motion.button 
                    type="submit" 
                    className="loginpet-btn loginpet-btn-primary mb-3"
                    whileHover={{ scale: 1.03 }}
                    whileTap={{ scale: 0.98 }}
                    disabled={isLoading}
                  >
                    {isLoading ? (
                      <>
                        <i className="fas fa-spinner fa-spin me-2"></i> Procesando...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-sign-in-alt me-2"></i> Iniciar Sesión
                      </>
                    )}
                  </motion.button>
                </motion.div>

                <motion.div 
                  className="loginpet-divider"
                  variants={itemVariants}
                >
                  o
                </motion.div>

                <motion.div 
                  className="text-center"
                  variants={itemVariants}
                >
                  <p className="loginpet-text-muted">
                    ¿Primera vez en PetCare?{' '}
                    <a href="/registro" className="loginpet-link">
                      Crear cuenta
                    </a>
                  </p>
                </motion.div>
              </form>
            </motion.div>
          </motion.div>

          {/* Imagen lateral */}
          <motion.div 
            className="col-lg-7 d-none d-lg-block ps-lg-4"
            variants={imageVariants}
            initial="hidden"
            animate="visible"
          >
            <div className="loginpet-image-container">
              <motion.img 
                src="https://images.unsplash.com/photo-1422565096762-bdb997a56a84?fm=jpg&q=60&w=3000"
                alt="Perro feliz recibiendo cuidados"
                className="loginpet-featured-image"
                whileHover={{ scale: 1.02 }}
                transition={{ type: "spring", stiffness: 200 }}
              />
              <div className="loginpet-image-caption">
                <i className="fas fa-paw me-2"></i> Cuidado profesional para tu mejor amigo
              </div>
            </div>
          </motion.div>
        </div>
      </div>
    </motion.div>
  );
}

export default Login;
