import React, { useState, useEffect } from 'react';

const PET_API = 'http://localhost:8090/bd_petcare/api/pets';
const OWNER_API = 'http://localhost:8090/bd_petcare/api/owners';

function PetForm() {
  const [formData, setFormData] = useState({
    name: '',
    species: '',
    breed: '',
    birthDate: '',
    owner: {
      ownerId: ''
    }
  });

  const [owners, setOwners] = useState([]);
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    fetch(`${OWNER_API}/all`)
      .then(res => res.json())
      .then(setOwners)
      .catch(err => console.error('Error cargando dueños:', err));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === 'ownerId') {
      setFormData(prev => ({
        ...prev,
        owner: {
          ...prev.owner,
          ownerId: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch(`${PET_API}/save`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
      .then(res => {
        if (res.ok) {
          setSuccessMessage('Mascota registrada correctamente');
          setFormData({
            name: '',
            species: '',
            breed: '',
            birthDate: '',
            owner: { ownerId: '' }
          });
        } else {
          return res.text().then(text => { throw new Error(text) });
        }
      })
      .catch(err => {
        console.error('Error al registrar mascota:', err);
        setSuccessMessage('Error al registrar mascota');
      });
  };

  return (
    <div>
      <h2>Registrar Nueva Mascota</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre:</label>
          <input name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Especie:</label>
          <input name="species" value={formData.species} onChange={handleChange} required />
        </div>
        <div>
          <label>Raza:</label>
          <input name="breed" value={formData.breed} onChange={handleChange} required />
        </div>
        <div>
          <label>Fecha de nacimiento:</label>
          <input type="date" name="birthDate" value={formData.birthDate} onChange={handleChange} required />
        </div>
        <div>
          <label>Dueño:</label>
          <select name="ownerId" value={formData.owner.ownerId} onChange={handleChange} required>
            <option value="">Seleccione un dueño</option>
            {owners.map(owner => (
              <option key={owner.ownerId} value={owner.ownerId}>
                {owner.ownerId} - {user.name}
              </option>
            ))}
          </select>
        </div>
        <button type="submit">Registrar</button>
      </form>

      {successMessage && <p>{successMessage}</p>}
    </div>
  );
}

export default PetForm;
